'use strict'
const urlencode = require('urlencode')

class qr_maker {
  static index(string) {
    const encoded_string = urlencode(string)
    return 'https://chart.googleapis.com/chart?cht=qr&chl='+encoded_string+'&chs=480x480&choe=UTF-8&chld=L|1'
  }
}

module.exports = qr_maker.index
